    def resistorvalue1(self):

        value2 = int(self.R1_value.text())
        text2 = "R1 {}Ω".format(value2)
        if value2 == 0:
            self.label_6.setText("R1")
            self.label_6.setGeometry(QtCore.QRect(610, 120, 100, 20))
        elif value2 !=0:
            self.label_6.setGeometry(QtCore.QRect(610, 120, 100, 20))
            self.label_6.setText(text2)
        return value2

    def resistorvalue2(self):

        value =int(self.C1_value.text())
        text2 = "R2 {}Ω".format(value)
        if value == 0:
            self.label_10.setText("R2")
            self.label_10.setGeometry(QtCore.QRect(830, 330, 100, 20))
        elif value !=0:
            text = "R2 {}Ω".format(value)
            self.label_10.setGeometry(QtCore.QRect(830, 330, 100, 20))
            self.label_10.setText(text2)
        return value

    def Frequency(self):
        value = int(self.lineEdit.text())
        return value
    def Amplitude(self):
            valu2= int(self.lineEdit_2.text())
            return valu2


    def voltage_measured1(self):
        return (self.resistorvalue1()/(self.resistorvalue1() + self.resistorvalue2())) * self.Amplitude()

    def voltage_measured2(self):
        return (self.resistorvalue2()/(self.resistorvalue1() + self.resistorvalue2())) * self.Amplitude()
    # def voltage_measured00(self):
    #     return  self.Amplitude()
    def showR1properties(self):

        Vrms = self.voltage_measured1() * 0.7071
        Vavg = round(self.voltage_measured1() * 0.637, 3)
        Irms = round(float(Vrms / self.resistorvalue1()), 3)
        self.Multimeter_value_displayer.setText(
            " R1 = {0}Ω \n Vrms ={1}v \n Vavg ={2}v \n Irms ={3}A".format(self.resistorvalue1(), round(Vrms, 3), Vavg, Irms))

    def showR2properties(self):
        Vrms = self.voltage_measured2()*0.7071
        Vavg = round(self.voltage_measured2()*0.637, 3)
        Irms = round(float(Vrms/self.resistorvalue2()), 3)
        self.Multimeter_value_displayer.setText(" R2 = {0}Ω \n Vrms ={1}v \n Vavg ={2}v \n Irms ={3}A".format(self.resistorvalue2(),round(Vrms, 3) , Vavg, Irms))


    def set_button_value(self, c):
        if c == 1:
            self.R1_left_label = 1
        elif c == 2:
            self.R1_right_label = 1
        if c == 3:
            self.V1_top_label = 1
        elif c == 4:
            self.V1_bottom_label = 1
        if c == 5:
            self.V2_top_label = 1
        elif c == 6:
            self.V2_bottom_label = 1
        self.all_button_clicked()


    def all_button_clicked(self):
        if self.R1_left_label == 1 and self.V1_top_label == 1:
            self.label_24.show()
            self.label_21.show()
            self.label_29.show()
        if self.R1_right_label == 1 and self.V2_top_label == 1:
            self.label_23.show()
            self.label_25.show()
            self.label_35.show()
        if self.V1_bottom_label == 1 and self.V2_bottom_label == 1:
            self.label_27.show()
            self.label_28.show()
            self.label_16.show()
            self.label_20.show()


