    def graph_draw(self,i):

        self.i = np.linspace(0, 1, i)
        self.fig = plt.figure()
        ax = self.fig.add_subplot(111)
        self.fig.patch.set_facecolor("#b2b5a3")
        ax.patch.set(facecolor="#41a8aa")
        plt.xlabel("                                           time")
        plt.grid(True)
        print("4")


